import React from 'react'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'
import * as BooksAPI from './BooksAPI'
import Book from './Book'


class Search extends React.Component {
    state = {
        query: '',
        newBooks: [],
        error: false
    }

    searchBook = query => {
        this.setState({ query })
        if (query) {
            BooksAPI.search(query).then(searched => {
                console.log(searched)
                searched && searched.length > 0 ?
                    this.setState({ newBooks: searched, error: false }) : this.setState({ newBooks: [], error: true })
            })
        } else { this.setState({ newBooks: [], error: false }) }
    }


    render() {
        const { query, newBooks } = this.state
        const { books, updateBookShelf, error } = this.props

        return (
            <div className="search-books">
                <div className="search-books-bar">
                    <Link className="close-search" to="/" >Close</Link>
                    <div className="search-books-input-wrapper">
                        <input type="text" name='query' placeholder="Search by title or author"
                            value={query} onChange={(event) => this.searchBook(event.target.value)} />
                    </div>
                </div>
                <div className="search-books-results">
                    {newBooks.length > 0 && (
                        <ol className="books-grid" key>
                            {newBooks.map(book => (<Book key={book.id} books={books} book={book} updateBookShelf={updateBookShelf} />))}
                        </ol>
                    )}
                    {error && (<h4>Your search keyword(s) did not found anything!</h4>)}
                </div>
            </div>
        )
    }
}

Search.propTypes = {
    books: PropTypes.array.isRequired,
    updateBookShelf: PropTypes.func.isRequired
}

export default Search
