import React from 'react'
import Book from './Book'
import PropTypes from 'prop-types'


const BookList = props => {
    const { books, updateBookShelf } = this.props
    const shelves = [
        { type: 'currReading', title: 'Currently Reading' },
        { type: 'wannaRead', title: 'Want to Read' },
        { type: 'read', title: 'Read' }
    ]

    return (
        <div className="list-books-content">
            {shelves.map((shelf, index) => {
                const booksShelf = books.filter(book => book.shelf === shelf.type)
            })}
            <div className="bookshelf">
                <h2 className="bookshelf-title">Currently Reading</h2>
                <div className="bookshelf-books">
                    <ol className="books-grid">
                        {currReading.map(book => (<Book key={book.id} book={book} onUpdateShelf={this.updateBookShelf} />))}
                    </ol>
                </div>
            </div>           
        </div>
    )
}


BooksList.propTypes = {
    books: PropTypes.array.isRequired,
    updateBookShelf: PropTypes.func.isRequired
}

export default BooksList
