import React from 'react'
// import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'
import Book from './Book'


class SearchBook extends React.Component {
    state = {
        query: ''
    }

    // onSearchBook = (query) => {
    //     this.setState(() => ({
    //         query: query.trim()
    //     }))
    // }

    handleInputChange = query => {
        this.setState(currState => ({
            query: query.trim()
        }))
    }
    clearQuery = () => {
        this.updateQuery('')
    }


    render() {
        const { query } = this.state
        const searchedBooks = query === ''
            ? []
            : this.props.books.filter((c) => (
                c.title.toLowerCase().includes(query.toLowerCase()) || c.author.toLowerCase().includes(query.toLowerCase())
            ))

        return (
            <div className="search-books">
                <div className="search-books-bar">
                    <Link className="close-search" to="/" >Close</Link>
                    <div className="search-books-input-wrapper">
                        <input type="text" name='query' placeholder="Search by title or author"
                            value={query} onChange={(event) => this.handleInputChange(event.target.value)} />
                    </div>
                </div>
                <div className="search-books-results">
                    <ol className="books-grid">
                        {searchedBooks.map(book => (<Book key={book.id} book={book} onUpdateShelf={this.updateBookShelf} />))}
                    </ol>
                </div>
            </div>
        )
    }
}

export default SearchBook
