import React, { Component } from 'react'
import PropTypes from 'prop-types'


class Book extends Component {
    state = {
        shelves: [
            { key: 0, value: "move", display: "Move to...", selected: false },
            { key: 1, value: "currentlyReading", display: "Currently Reading", selected: false },
            { key: 2, value: "wantToRead", display: "Want to Read", selected: false },
            { key: 3, value: "read", display: "Read", selected: false },
            { key: 4, value: "none", display: "None", selected: false }
        ]
    }

    handleSelect = (option) => {
        console.log(option)
        this.setState(state => ({
            shelves: state.shelves.map(shelf => {
                return {
                    ...shelf,
                    selected: shelf.value === option
                }
            })
        }))
        this.props.onUpdateShelf(option)
    }

    render() {
        const { book } = this.props
        return (
            <li>
                <div className="book">
                    <div className="book-top">
                        <div className="book-cover" style={{ width: 128, height: 192, backgroundImage: `url(${book.imageLinks.thumbnail})` }}></div>
                        <div className="book-shelf-changer">
                            <select className="book-shelf-select" value={book.shelf} onChange={option => this.handleSelect(option.target.value)} >
                                {this.state.shelves.map(s => (
                                    (s.key === 0) ? (<option key={s.key} value={s.value} disabled>{`${s.display}`}</option>) : (
                                        (s.value === book.shelf) ? (<option key={s.key} value={s.value}>&#10003;  {`${s.display}`}</option>) : (
                                            <option key={s.key} value={s.value}>   {`${s.display}`}</option>)
                                    )
                                ))}
                            </select>
                        </div>
                    </div>
                    <div className="book-title"><p>{book.title}</p></div>
                    <div className="book-authors">{book.authors}<br></br> </div>
                    <div className="book-authors">
                        {book.authors.map((author) => (
                            <p key={author}>{author}</p>
                        ))}
                    </div>
                </div>
            </li>
        )
    }
}

Book.propTypes = {
    book: PropTypes.object.isRequired,
}

export default Book
