import React, { Component } from 'react'
import PropTypes from 'prop-types'


class Book extends Component {
    state = {
        shelves: [
            { key: 0, value: "move", display: "Move to...", selected: false },
            { key: 1, value: "currentlyReading", display: "Currently Reading", selected: false },
            { key: 2, value: "wantToRead", display: "Want to Read", selected: false },
            { key: 3, value: "read", display: "Read", selected: false },
            { key: 4, value: "none", display: "None", selected: false }
        ]
    }

    updateShelf = (shelf) => {
        console.log(shelf)
        this.setState(currState => ({
            shelf: currState.shelf
        }))
        this.state.shelf = shelf
        this.props.onUpdateShelf(this.state.shelf)
    }

    render() {
        const { book } = this.props
        // const selected = this.state.shelves.find(shelf => shelf.selected)
        return (
            <li>
                <div className="book">
                    <div className="book-top">
                        <div className="book-cover" style={{ width: 128, height: 192, backgroundImage: `url(${book.imageLinks.thumbnail})` }}></div>
                        <div className="book-shelf-changer">
                            <select value={this.state.shelf} onSelect={option => this.handleSelect(option)} optionLabelProp={"value"} onChange={(event) => this.updateShelf(event.target.value)}>
                                {this.state.shelves.map(e => (
                                    <option key={e.key} value={e.value}>{`${e.display}`}</option>
                                    // <Option key={e.key} value={e.value} title="test">
                                    //     {`${e.display} | ${e.value}`}
                                    // </Option>
                                ))}
                                {/* <option value="move" disabled>Move to...</option>
                                <option value="currentlyReading">Currently Reading</option>
                                <option value="wantToRead">Want to Read</option>
                                <option value="read">Read</option>
                                <option value="none">None</option> */}
                            </select>
                        </div>
                    </div>
                    <div className="book-title">{book.title}</div>
                    <div className="book-authors">{book.authors}</div>
                </div>
            </li>
        )
    }
}

Book.propTypes = {
    book: PropTypes.object.isRequired,
}

export default Book
