import React from 'react'
import Book from './Book'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'


class BooksList extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            currReading: [],
            wannaRead: [],
            read: [],
        }
    }


    componentWillReceiveProps(props) {
        console.log('All', props.books)
        this.setState(currState => ({
            currReading: props.books.filter((book) => (
                book.shelf === 'currentlyReading'
            ))
        }))
        this.setState(currState => ({
            wannaRead: props.books.filter((book) => (
                book.shelf === 'wantToRead'
            ))
        }))

        this.setState(currState => ({
            read: props.books.filter((book) => (
                book.shelf === 'read'
            ))
        }))
    }

    updateBookShelf = (id, value, target) => {
        // this.setState(currState => ({
        //     books: currState.books
        // }));
        const oldCurrReading = this.state.currReading.filter(book => book.id === id)
        const oldWannaRead = this.state.wannaRead.filter(book => book.id === id)
        const oldRead = this.state.read.filter(book => book.id === id)
        console.log(id, value, target)
        console.log(oldCurrReading, oldWannaRead, oldRead)
        if (oldCurrReading.length) {
            // this.state.currReading.filter(book => book.id !== id)
            this.removeFromShelf(this.state.currReading, id)
        }
        if (oldWannaRead.length) {}
        if (oldRead.length) {}
        console.log(this.state.currReading)
    }

    removeFromShelf = (shelf, bookId) => { return shelf.filter(book => book.id !== bookId)}

    render() {
        const { currReading, wannaRead, read } = this.state

        return (
            <div className="list-books-content">
                <div className="open-search">
                    <Link to="/search" className="open-search-link">Add a book</Link>
                </div>
                <div className="bookshelf">
                    <h2 className="bookshelf-title">Currently Reading</h2>
                    <div className="bookshelf-books">
                        <ol className="books-grid">
                            {currReading.map(book => (<Book key={book.id} book={book} onUpdateShelf={this.updateBookShelf} />))}
                        </ol>
                    </div>
                </div>
                <div className="bookshelf">
                    <h2 className="bookshelf-title">Want to Read</h2>
                    <div className="bookshelf-books">
                        <ol className="books-grid">
                            {wannaRead.map(book => (<Book key={book.id} book={book} onUpdateShelf={this.updateBookShelf} />))}
                        </ol>
                    </div>
                </div>
                <div className="bookshelf">
                    <h2 className="bookshelf-title">Read</h2>
                    <div className="bookshelf-books">
                        <ol className="books-grid">
                            {read.map(book => (<Book key={book.id} book={book} onUpdateShelf={this.updateBookShelf} />))}
                        </ol>
                    </div>
                </div>
            </div>
        )
    }
}

BooksList.propTypes = {
    books: PropTypes.array.isRequired,
}

export default BooksList
