import React from 'react'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'
import Book from './Book'


class Search extends React.Component {
    state = {
        query: '',
        newBooks: [],
    }

    // onSearchBook = (query) => {
    //     this.setState(() => ({
    //         query: query.trim()
    //     }))
    // }

    handleInputChange = query => {
        this.setState({ query })
    }
    clearQuery = () => {
        this.updateQuery('')
    }


    render() {
        const { query, newBooks } = this.state
        const { books, updateBookShelf } = this.props

        return (
            <div className="search-books">
                <div className="search-books-bar">
                    <Link className="close-search" to="/" >Close</Link>
                    <div className="search-books-input-wrapper">
                        <input type="text" name='query' placeholder="Search by title or author"
                            value={query} onChange={(event) => this.handleInputChange(event.target.value)} />
                    </div>
                </div>
                <div className="search-books-results">
                    <ol className="books-grid">
                        {newBooks.map(book => (<Book key={book.id} book={book} onUpdateShelf={this.updateBookShelf} />))}
                    </ol>
                </div>
            </div>
        )
    }
}

export default Search
